// Wii miis
export type WiiMii = {
    general: {
        gender: number;
        birthMonth: number;
        birthday: number;
        favoriteColor: number;
        height: number;
        weight: number;
    };
    meta: {
        name: string;
        creatorName: string;
    };
    perms: {
        mingle: boolean;
        fromCheckMiiOut: boolean;
    };
    face: {
        type: number;
        color: number;
        feature: number;
    };
    hair: {
        page: string;
        type: string;
        color: number;
        flipped: boolean;
    };
    eyebrows: {
        page: string;
        type: string;
        rotation: number;
        color: number;
        size: number;
        yPosition: number;
        distanceApart: number;
    };
    eyes: {
        page: string;
        type: string;
        rotation: number;
        yPosition: number;
        color: number;
        size: number;
        distanceApart: number;
    };
    nose: {
        type: string;
        size: number;
        yPosition: number;
    };
    mouth: {
        page: string;
        type: string;
        color: number;
        size: number;
        yPosition: number;
    };
    glasses: {
        type: number;
        color: number;
        size: number;
        yPosition: number;
    };
    beard: {
        mustache: {
            type: number;
            size: number;
            yPosition: number;
        };
        type: number;
        color: number;
    };
    mole: {
        on: boolean;
        size: number;
        yPosition: number;
        xPosition: number;
    };
};
